// Extract and validate report parameters from app
const webhookData = $input.first().json.body || $input.first().json;

// Extract report parameters
const reportParams = {
  title: webhookData.title || 'Untitled Report',
  dataSource: webhookData.dataSource || '',
  reportType: webhookData.reportType || 'analysis',
  includeCharts: webhookData.includeCharts !== false, // default true
  format: webhookData.format || 'pdf',
  timestamp: new Date().toISOString(),
  executionId: 'report_' + Date.now()
};

// Validate required fields
if (!reportParams.dataSource || reportParams.dataSource.length < 20) {
  throw new Error('Data source is required and must be at least 20 characters');
}

// Create comprehensive prompt for OpenAI
let openAIPrompt = `Create a comprehensive ${reportParams.reportType} report with the following specifications:

Title: ${reportParams.title}
Report Type: ${reportParams.reportType}
Include Charts: ${reportParams.includeCharts ? 'Yes' : 'No'}
Output Format: ${reportParams.format}

DATA SOURCE:
${reportParams.dataSource}

PLEASE CREATE A PROFESSIONAL REPORT WITH:

1. EXECUTIVE SUMMARY
   - Key findings and recommendations
   - Critical insights overview

2. DETAILED ANALYSIS
   - Data breakdown and interpretation
   - Trends and patterns identification
   - Statistical insights where applicable

3. FINDINGS & INSIGHTS
   - Main discoveries from the data
   - Comparative analysis if relevant
   - Performance metrics and KPIs

4. RECOMMENDATIONS
   - Actionable next steps
   - Strategic recommendations
   - Risk mitigation strategies

5. CONCLUSION
   - Summary of key points
   - Future outlook
   - Implementation timeline

${reportParams.includeCharts ? '\n6. CHARTS & VISUALIZATIONS\n   - Describe recommended charts/graphs\n   - Data visualization suggestions\n   - Key metrics to highlight visually' : ''}

Please format the report professionally with clear headings, bullet points, and structured content. Make it comprehensive yet readable.`;

return {
  ...reportParams,
  openAIPrompt: openAIPrompt,
  originalRequest: webhookData
};

// Extract OpenAI response and prepare for Google Docs
const data = $input.first().json;

// Debug: Log the incoming data structure
console.log('Incoming data structure:', JSON.stringify(data, null, 2));

// Initialize variables
let reportContent = 'Report generation failed';
let title = 'Business Report';
let reportType = 'Analysis';
let executionId = 'report_' + Date.now();
let timestamp = new Date().toISOString();

// Try to extract the OpenAI response content
// Check multiple possible response formats from the OpenAI node
if (data.text) {
    // Direct text response
    reportContent = data.text;
} else if (data.content) {
    // Content field
    reportContent = data.content;
} else if (data.choices && Array.isArray(data.choices) && data.choices[0]) {
    // Standard OpenAI API response format
    if (data.choices[0].message && data.choices[0].message.content) {
        reportContent = data.choices[0].message.content;
    } else if (data.choices[0].text) {
        reportContent = data.choices[0].text;
    }
} else if (Array.isArray(data) && data.length > 0) {
    // Array format response
    if (data[0].message && data[0].message.content) {
        reportContent = data[0].message.content;
    } else if (data[0].text) {
        reportContent = data[0].text;
    } else if (data[0].content) {
        reportContent = data[0].content;
    }
} else if (data.message && data.message.content) {
    // Direct message format
    reportContent = data.message.content;
}

// Try to get metadata from the previous nodes
// Look for data from the "Prepare Report Data" node
const allInputs = $input.all();
let reportMetadata = {};

// Check if we have access to the original report parameters
allInputs.forEach(input => {
    const inputData = input.json;
    if (inputData.title) reportMetadata.title = inputData.title;
    if (inputData.reportType) reportMetadata.reportType = inputData.reportType;
    if (inputData.executionId) reportMetadata.executionId = inputData.executionId;
    if (inputData.timestamp) reportMetadata.timestamp = inputData.timestamp;
});

// Use metadata if available
title = reportMetadata.title || data.title || 'Business Report';
reportType = reportMetadata.reportType || data.reportType || 'Analysis';
executionId = reportMetadata.executionId || data.executionId || 'report_' + Date.now();
timestamp = reportMetadata.timestamp || data.timestamp || new Date().toISOString();

// Validate that we got content
if (reportContent === 'Report generation failed') {
    console.error('Failed to extract report content. Available keys:', Object.keys(data));
    // You might want to throw an error here or use a fallback
    reportContent = `Error: Could not extract report content from OpenAI response.\n\nDebug Info:\n${JSON.stringify(data, null, 2)}`;
}

// Create document title with timestamp
const docTitle = `${title} - ${new Date().toLocaleDateString()}`;

// Add header to the report (only if we have valid content)
let finalReport;
if (reportContent !== 'Report generation failed' && !reportContent.startsWith('Error:')) {
    finalReport = `${title}\n${'='.repeat(50)}\n\nGenerated: ${new Date().toLocaleString()}\nReport Type: ${reportType}\nExecution ID: ${executionId}\n\n${reportContent}\n\n${'='.repeat(50)}\nGenerated by AI Assistant`;
} else {
    // If we failed to get content, just pass the error message
    finalReport = reportContent;
}

return {
    title: title,
    documentTitle: docTitle,
    reportContent: finalReport,
    executionId: executionId,
    timestamp: timestamp,
    // Add debug info
    debugInfo: {
        originalDataKeys: Object.keys(data),
        contentExtracted: reportContent !== 'Report generation failed'
    }
};

